//==============================//==============================//==============================//==============================//==============================
 contract DEFIAT is Context, IERC20 { 

    using SafeMath for uint; 
    using Address for address;

//ERC20 Setup
    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;
    uint256 public _totalSupply; string public _name; string public _symbol; uint8 public _decimals;
    uint256 public mintEvent;

//Governance
    address public mastermind; //address in charge of managing minters' list
    mapping (address => uint256) private _actorLevel; //multi-tier level

    uint256 public antiBotDelayBlock; //forces users to wait between 2 transactions. Default = 5 blocks.

//Burn and Fees
    uint256 public burnRate; //% rate of burn at each transaction
    uint256 public feeRate;
    uint256 public txThreshold; //min tansfer to generate Loyalty points
    address private feeDestination; //target address for fees (will be the staking contract)

//loyalty points
    mapping (uint => uint256) private _discountTranches;
    mapping (address => uint256) private _discounts;
    mapping (address => uint256) private _loyaltyPoints; //sum of all the tx done so far (threshold)
    uint256 private _allLoyaltyPoints; //megacounter, used for burn events.

//Events
    event stdEvent(address _txOrigin, uint256 _number, bytes32 _signature, string _desc);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

 //Token generation
    constructor () public { 
        _name = "Kovan_DeFiaT";
        _symbol = "kDFT";
        _decimals = 10;
        _totalSupply = 0; //token needs to be minted
        feeRate = 15; // 0.5% fee to be redistributed
        burnRate = 5; // 0.5% burn (deflation)
        feeDestination = msg.sender; //initial fees setup to contract owner. 
        mintEvent = 0;

        mastermind = msg.sender;
        _actorLevel[msg.sender] = 3; //contract owner. Can be delegated later
    }


function initializeToken() public onlyMasterMind {       // TESTNET ONLY
    setAll10DiscountTranches(5, 10, 50, 100, 250, 500, 1500, 2000, 10000, 9999999999);
    mint(mastermind, 5000000000000000);
    updateTxTreshold(10000000000); //1 token(s) to get loyalty points
    changeDiscount(mastermind, 100);// gives mastermind 100% discount (to setup UNISWAP)
    overrideLoyaltyPoints(mastermind, 100000); //gives points
}

// //Public View
    //Token information
    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }
    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    //Token Specific
    function currentBurnRate() public view returns (uint256) {
        return burnRate; 
    }
    function currentfeeRate() public view returns (uint256) {
        return feeRate;
    }
    function currentLoyaltyCounter() public view returns (uint256) {
        return _allLoyaltyPoints;
    }

    //User Loyalty specific
    function showLoyaltyPointsOf(address account) public view returns (uint256) {
        return _loyaltyPoints[account]; //Loyalty points grants discounts and more stuff
    }
    function showLevelOf(address _address) public view returns (uint256) {
        return _actorLevel[_address]; //address lvl (3, 2, 1 or 0)
    }   
    function currentDiscountOf(address _address) public view returns (uint256) {
        return _discounts[_address];
    }
    function discountTrancheLoyaltyNeeded(uint _tranche) public view returns (uint256) {
        return _discountTranches[_tranche]; //check the nb of points needed to access discount tranche
    }
    function discountEligibilityOf(address _address, uint _tranche) public view returns (uint) {
    //updates the discount for a specific address based on historical Loyalty
       if(_loyaltyPoints[_address] >= _discountTranches[_tranche]) {
           return SafeMath.mul(_tranche,10); }
       else {
           return 0;}
        }
    function updatemyDiscount(address _address, uint _tranche) public returns (bool) {
        //users execute this function to upgrade their status level, asssuming Eligibility.
        require(discountEligibilityOf(_address, _tranche) > _discounts[_address]); //new discount needs to be higher than old one
        _discounts[_address] =  discountEligibilityOf(_address, _tranche); //update of discount
        return true;
    }

//Transfer function
    function transfer(address recipient, uint256 amount) public override virtual returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

//Allowance Management
    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }
    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }
    function transferFrom(address sender, address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }
    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }
    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }    

//INTERNAL FUNCTIONS
 //This is the main function for transfer. Where burns and fees are collected and loyalty points accrued

    function _transfer(address sender, address recipient, uint256 amount) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

    //get discounts and apply them. You get the MAX discounts of the sender x recipient. discount is base100
        uint256 __discount = SafeMath.max(_discounts[sender], _discounts[recipient]);//max discount applied.
        if(__discount > 100){__discount = 100;} //manages "forever pools"
        uint256 dAmount = SafeMath.div( SafeMath.mul(amount,SafeMath.sub(100,__discount)),100);     //amount discounted to calculate fees

    //Calculates burn and fees on discounted amount (burn and fees are 0.X% ie base 1000)
        uint _toBurn = SafeMath.div(SafeMath.mul(dAmount,burnRate),1000); 
        uint _toFee = SafeMath.div(SafeMath.mul(dAmount,feeRate),1000); 
        uint _amount = SafeMath.sub(amount, SafeMath.add(_toBurn,_toFee)); //calculates the remaning amount to be sent

    //transfer fee
        if(_toFee > 0) {
        _beforeTokenTransfer(sender, feeDestination, _toFee);
        _balances[sender] = _balances[sender].sub(_toFee, "ERC20: transfer Fee exceeds balance");
        _balances[feeDestination] = _balances[feeDestination].add(_toFee);
        emit Transfer(sender, feeDestination, _toFee); //so it's visible on-chain
        }

    //burn
        if(_toBurn > 0) {_burn(msg.sender,_toBurn);}

    //transfer remaining amount
        _beforeTokenTransfer(sender, recipient, _amount);
        _balances[sender] = _balances[sender].sub(_amount, "ERC20: transfer amount exceeds balance");
        _balances[recipient] = _balances[recipient].add(_amount);
        emit Transfer(sender, recipient, _amount);

    //Update user's loyalty points
        if(amount >= txThreshold && sender != recipient) {addLoyaltyPoints(sender, 1);} 

    }

    function addLoyaltyPoints(address _address, uint256 _points) internal virtual {
        _loyaltyPoints[_address]= SafeMath.add(_loyaltyPoints[_address], _points); 
        _allLoyaltyPoints = SafeMath.add(_allLoyaltyPoints, _points); //updates megacounter
    }




    //higher in code?
    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");
        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        _balances[account] = _balances[account].sub(amount, "ERC20: burn amount exceeds balance");
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual { } //Jesus saves... gas



// --GOVERNANCE --  Functions for the management of the list of allowed address "governors" and partners.)

//Modifiers
    modifier onlyMasterMind {
    require(msg.sender == mastermind, " only Mastermind");
    _;
    }
    modifier onlyGovernor {
    require(_actorLevel[msg.sender] >= 2,"only Governors");
    _;
    }
    modifier onlyPartner {
    require(_actorLevel[msg.sender] >= 1,"only Partners");
    _;
    }

//MasterMind (lvl 3)
    function killContract() public onlyMasterMind {
        require(msg.sender == mastermind, " only mastermind"); //twice!
        selfdestruct(msg.sender); //destroys the contract
    } //only MasterMind can kill contract (testnetonly)

    function setMastermind(address _mastermind) public onlyMasterMind {
      mastermind = _mastermind; //Only one mastermind.
      _actorLevel[msg.sender] = 2; //new level for previous mastermind
      emit stdEvent(address(tx.origin), 0, keccak256(abi.encodePacked(_mastermind)), "MasterMind Changed");
    }     //only MasterMind can transfer his own rights

    function mint(address account, uint amount) public onlyMasterMind {
      require(mintEvent == 0);
      _mint(account, amount);
      mintEvent = block.number; //locks the minting forever.
    } //only mastermind can mint tokens manually. Can only happen ONCE and generates the mintEvent.

    function changeFeeDest(address _feeDestination) public onlyMasterMind {
      feeDestination = _feeDestination;
    emit stdEvent(address(tx.origin), 0, keccak256(abi.encodePacked(_feeDestination)), " Fee Destination Changed");
    } //only mastermind can change feeDestination addresss

    function removeAllRights(address _address) public onlyMasterMind {
      require(_address != mastermind);
      _actorLevel[_address] = 0; //removes all rights
      emit stdEvent(address(_address), 0, keccak256(abi.encodePacked(tx.origin)), "Rights Revoked");
    }

    //get tokens sent by error to contract
    function widthdrawAnyToken(address _recipient, address payable _ERC20address, uint256 _amount) public onlyMasterMind returns (bool) {
        IERC20(_ERC20address).transfer(_recipient, _amount); //use the IERC20 traditional transfer
        return true;
    }



 //Governors (Lvl 2)

   function currentFeeDest() public view onlyGovernor returns (address)  {
        return feeDestination; 
    }

  //only approved governors (level 2) can update txThreshold
    function updateTxTreshold(uint amount) public onlyGovernor {
      require(_actorLevel[msg.sender]>=2, "only lvl 2 governors");
      txThreshold = amount; 
    }

    //only governors can force and update new discounts for some users. up to 100%
    function changeDiscount(address _address, uint256 _newDiscount) public onlyGovernor {
      require(_newDiscount <= 100); //100 = 100% discount
      require(_actorLevel[_address]>=1, "Must be a partner address");
      _discounts[_address]  = _newDiscount;
      emit stdEvent(address(_address), _newDiscount, keccak256(abi.encodePacked(tx.origin)), "Discount Changed");
    }

    //only governors can change burnRate/tx
    function changeBurnRate(uint _burnRate) public onlyGovernor {
      require(_actorLevel[msg.sender]>=2, "only lvl 2 governors");
      require(_burnRate <=200, "reaching limit"); //cannot burn more than 20.0%
      burnRate = _burnRate; 
      emit stdEvent(address(msg.sender), _burnRate, keccak256(abi.encodePacked(tx.origin)), "BurnRate Changed");
    }

    //only governors can change feeRate/tx
    function changeFeeRate(uint _feeRate) public onlyGovernor {
      require(_actorLevel[msg.sender]>=2, "only governors");
      require(_feeRate >=1, "reaching limit"); //cannot take less than 0.1% fees
      require(_feeRate <=200, "reaching limit"); //cannot take more than 20.0% fees
      feeRate = _feeRate;
      emit stdEvent(address(msg.sender), _feeRate, keccak256(abi.encodePacked(tx.origin)), "FeeRate Changed");
    }

    function setAll10DiscountTranches(
            uint256 _pointsNeeded1, uint256 _pointsNeeded2, uint256 _pointsNeeded3, uint256 _pointsNeeded4, 
            uint256 _pointsNeeded5, uint256 _pointsNeeded6, uint256 _pointsNeeded7, uint256 _pointsNeeded8, 
            uint256 _pointsNeeded9, uint256 _pointsNeeded10) public onlyGovernor {

        _discountTranches[0] = 0;
        _discountTranches[1] = _pointsNeeded1; //10%
        _discountTranches[2] = _pointsNeeded2; //20%
        _discountTranches[3] = _pointsNeeded3; //30%
        _discountTranches[4] = _pointsNeeded4; //40%
        _discountTranches[5] = _pointsNeeded5; //50%
        _discountTranches[6] = _pointsNeeded6; //60%
        _discountTranches[7] = _pointsNeeded7; //70%
        _discountTranches[8] = _pointsNeeded8; //80%
        _discountTranches[9] = _pointsNeeded9; //90%
        _discountTranches[10] = _pointsNeeded10; //90%

    }

    function overrideLoyaltyPoints(address _address, uint256 _newPoints) public onlyGovernor {
      _loyaltyPoints[_address]  = _newPoints; //forced
      emit stdEvent(address(_address), _newPoints, keccak256(abi.encodePacked(msg.sender)), "Loyalty points forced");
    }


//Partners (lvl 1)
    //only approved partners can burn tokens manually (ie impact the tokensupply)
    function burn(uint amount) public onlyPartner {
      _burn(msg.sender, amount);
    }

    //only Partners can Transfer loyalty Points to some users.
    //This will be used to give external contracts the right to give loyalty points to users, thus unlocking discounts.
    function transferLoyaltyPoints(address _address, uint256 _points) public onlyPartner {
        _loyaltyPoints[msg.sender]= SafeMath.sub(_loyaltyPoints[msg.sender], _points);
        addLoyaltyPoints(_address, _points);
        emit stdEvent(address(_address), _points, keccak256(abi.encodePacked(msg.sender)), "Loyalty points transfer");
    }

//Pleb (lvl 0)
    function setActorLevel(address _address, uint256 _newLevel) public onlyPartner{
      require(_newLevel < _actorLevel[msg.sender], "Can only give rights below you");
      _actorLevel[_address] = _newLevel; //updates level -> adds or removes rights
      emit stdEvent(address(_address), _newLevel, keccak256(abi.encodePacked(msg.sender)), "Level changed");
    }



} 
